import React, { useState } from 'react';

function CalculadoraAreaCuadrado() {
    const [lado, setLado] = useState('');
    const [area, setArea] = useState<number | null>(null);

    const calcularArea = () => {
    const ladoNumerico = parseFloat(lado);
    setArea(!isNaN(ladoNumerico) ? ladoNumerico * ladoNumerico : null);
};

    return (
    <div>
        <h2>Calculadora de Área de Cuadrado</h2>
        <input
        type="number"
        value={lado}
        onChange={(e) => setLado(e.target.value)}
        placeholder="Ingrese el lado del cuadrado"
        />
        <button onClick={calcularArea}>Calcular Área</button>
        {area !== null && <p>El área del cuadrado es: {area}</p>}
    </div>
);
}

export default CalculadoraAreaCuadrado;
