import React from 'react';

// Función para sumar los elementos de un arreglo
function sumarElementos(arreglo: number[]): number {
    return arreglo.reduce((suma: number, elemento: number) => suma + elemento, 0);
}

function SumadorArreglo(): JSX.Element {
    // Arreglo con datos fijos
    const datosArreglo: number[] = [1, 2, 3, 4, 5];

    // Calcular la suma
    const suma: number = sumarElementos(datosArreglo);

    return (
        <div>
            <h2>Sumador de Elementos del Arreglo</h2>
            <p>Arreglo: [{datosArreglo.join(', ')}]</p>
            <p>La suma de los elementos es: {suma}</p>
        </div>
    );
}

export default SumadorArreglo;
